# -*- coding: utf-8 -*-__author__ = "Lee.le"from Model.tools import *from django.shortcuts import renderdef progress(request):    """        显示测试用例内容        :param request:        :return:        """    if request.method == 'POST':        pass    config = configparser.ConfigParser()    config_Path = (os.path.abspath(os.path.join(os.getcwd(), "../multi_processframe/config.ini")))  # 设置配置路径    config.read(config_Path, encoding='utf-8-sig')    # config.read(path, encoding='GBK')    temp = config.get('config', "progress").split(",")    caselist = []    undonelist = []    for k, v in case_translate().items():        if v in temp:            caselist.append(k)        else:            undonelist.append(k)    return render(request, "case.html", {'addr': get_addr(), "caselist": caselist, 'data': case_translate(), 'undonelist':undonelist, 'done': len(caselist), 'undone': len(undonelist)})