# -*- coding: utf-8 -*-__author__ = "Lee.le"from Model.tools import *from django.shortcuts import render, reversefrom django.contrib.auth import authenticate, loginfrom django.http import HttpResponseRedirect, HttpResponsedef user_login(request):    if request.method == 'POST':        username = request.POST.get("username")        password = request.POST.get("password")        if username == 'admin' and password == '123':            return render(request, 'baseauto.html')        else:            return render(request, 'login.html')    else:        return render(request, 'login.html')